blocks.schema:
export type NavbarBlock = {
  type: "Navbar";
  logo: string;
  links: { label: string; href: string }[];
}

export type HeroBlock = {
  type: "Hero";
  headline: string;
  subheadline: string;
  ctaText: string;
}

export type FeaturesBlock = {
  type: "Features";
  title: string;
  features: { title: string; description: string }[];
}

export type PricingBlock = {
  type: "Pricing";
  title: string;
  plans: { 
    name: string; 
    price: string; 
    features: string[]; 
    ctaText: string; 
  }[];
}

export type FooterBlock = {
  type: "Footer";
  copyright: string;
  links: { label: string; href: string }[];
}

export type TableCRUDBlock = {
  type: "TableCRUD";
  model: string; 
}

export type FAQBlock = {
  type: "FAQ";
  title: string;
  questions: { question: string; answer: string }[];
}

export type TestimonialsBlock = {
  type: "Testimonials";
  title: string;
  testimonials: { name: string; role: string; quote: string }[];
}

export type ContactBlock = {
  type: "Contact";
  title: string;
  subtitle: string;
  buttonText: string;
}

export type StatsBlock = {
  type: "Stats";
  stats: { label: string; value: string }[];
}

export type CustomBlock = {
  type: "Custom";
  name: string; // e.g. "Carousel"
  code: string; // The full React component code (starting with 'export default function ComponentName() {')
}
